SELECT  pdh.news.id_news as id, pdh.news_landing.id_news_landing as id_landing, pdh.news_landing_lang.title, pdh.news_landing_lang.description as content, pdh.news_lang.url,pdh.news.is_extern,pdh.news.url_extern FROM pdh.news, pdh.news_lang, pdh.news_landing, pdh.news_landing_lang, pdh.news_house WHERE pdh.news_landing.id_news = pdh.news.id_news AND pdh.news_house.id_house= 6 AND pdh.news_landing.id_news_landing = pdh.news_landing_lang.id_news_landing AND pdh.news_lang.id_news = pdh.news.id_news AND pdh.news_landing_lang.lang = 'fr'  AND pdh.news_lang.lang = 'fr'  AND pdh.news.online = 1 and pdh.news.publish_on<='2017-12-12 14:15:03' ORDER BY publish_on DESC LIMIT 3 